// src/hooks/useFetchAlbums.js
import { useState, useCallback } from "react";
import axios from "axios";

const useFetchAlbums = () => {
  const [albums, setAlbums] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // useCallback para evitar recrear la funci칩n en cada render
  const fetchAlbums = useCallback(async (artistId) => {
    if (!artistId) return;

    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://api.deezer.com/artist/${artistId}/albums`
      );
      setAlbums(response.data.data);
    } catch (err) {
      setError("Error al obtener los 치lbumes");
      setAlbums([]);
    } finally {
      setLoading(false);
    }
  }, []);

  return { albums, fetchAlbums, loading, error };
};

export default useFetchAlbums;
