import { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const SearchResults = ({ albums, artistName, onAddToLibrary }) => {
  const [selectedAlbumId, setSelectedAlbumId] = useState(null);
  const [tracks, setTracks] = useState({});
  const [loading, setLoading] = useState(false);

  const toggleTracks = async (albumId) => {
    if (selectedAlbumId === albumId) {
      setSelectedAlbumId(null);
    } else {
      setSelectedAlbumId(albumId);
      if (!tracks[albumId]) {
        setLoading(true);
        try {
          const response = await axios.get(
            `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}/tracks`
          );
          setTracks((prev) => ({ ...prev, [albumId]: response.data.data }));
        } catch (error) {
          console.error(error);
        } finally {
          setLoading(false);
        }
      }
    }
  };

  return (
    <section>
      {albums.map((album) => (
        <article 
          key={album.id}>
          <img src={album.cover_medium} alt={album.title} />
          <p>{artistName}</p>
          <h3>{album.title}</h3>

          <button onClick={() => onAddToLibrary({ album, artistName })}>
            Agregar a biblioteca
          </button>

          <button onClick={() => toggleTracks(album.id)}>
            {selectedAlbumId === album.id ? "Ocultar canciones" : "Ver canciones"}
          </button>

          {selectedAlbumId === album.id && (
            <div>
              {loading ? (
                <p>Cargando canciones...</p>
              ) : (
                <ul>
                  {tracks[album.id]?.map((track) => (
                    <li key={track.id}>
                      <Link to={`/song/${track.id}`}>{track.title}</Link>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          )}
        </article>
      ))}
    </section>
  );
};

export default SearchResults;
