import './components/Header/styles.css';
import './components/SearchResults/styles.css';

import './components/Library/styles.css';

import React, { useState } from 'react';
import Header from './components/Header';
import Library from './components/Library';
import SearchResults from './components/SearchResults';
import axios from 'axios';
import ArtistList from './components/ArtistList';
import SearchInput from './components/SearchInput/';



const App = () => {
  const [search, setSearch] = useState('');
  const [artists, setArtists] = useState([]);
  const [albums, setAlbums] = useState([]);
  const [selectedArtistName, setSelectedArtistName] = useState('');
  const [librarySongs, setLibrarySongs] = useState([]);
  const [showResults, setShowResults] = useState(true);



  const handleSearch = async (e) => {
  const value = e.target.value;
  setQuery(value);

  if (value.length > 2) {
    const response = await axios.get(`https://cors-anywhere.herokuapp.com/https://api.deezer.com/search/artist?q=${value}`);
    setArtistSuggestions(response.data.data);
  } else {
    setArtistSuggestions([]);
  }
};



  const fetchAlbums = async (artistId, artistName) => {
    try {
      const response = await axios.get(`https://cors-anywhere.herokuapp.com/https://api.deezer.com/artist/${artistId}/albums`);
      console.log('Respuesta de álbumes:', response.data);
      const albumsData = response.data.data.map((album) => ({
        id: album.id,
        image: album.cover_medium,
        album: album.title,
        artist: artistName,
        releaseDate: album.release_date,
      }));
      setAlbums(albumsData);
      setSelectedArtistName(artistName);
    } catch (error) {
      console.error('Error al obtener álbumes:', error);
    }
  };


  const handleSelectArtist = async (artist) => {
  setSelectedArtist(artist); // Se guarda el artista
  setArtistSuggestions([]);  // Se ocultan las sugerencias

  const response = await axios.get(`https://api.deezer.com/artist/${artist.id}/albums`);
  setAlbums(response.data.data); // Se cargan los álbumes
};



  const handleAddToLibrary = (song) => {
    if (!librarySongs.some((s) => s.id === song.id)) {
      setLibrarySongs([...librarySongs, song]);
    }
  };

  return (
    <div className='App p-4 font-sans'>
      <Header />
      <SearchInput/>


      {artists.length > 0 && (
        <ArtistList artists={artists} onSelect={fetchAlbums} />
      )}

      {albums.length > 0 && (

        <SearchResults 
        albums={albums} 
        onAddToLibrary={handleAddToLibrary}
        onAlbumSelect={() => setShowResults(false)} />


      )}

      <Library songs={librarySongs} />
    </div>
  );
};


export default App;
