import { useState } from "react";
import axios from "axios";

const useFetchTracks = () => {
  const [tracks, setTracks] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchTracks = async (albumId) => {
    setLoading(true);
    setError(null);
    try {
      const url = `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}/tracks`;
      const response = await axios.get(url);
      setTracks((prev) => ({
        ...prev,
        [albumId]: response.data.data,
      }));
    } catch (err) {
      setError("Error al cargar canciones");
    } finally {
      setLoading(false);
    }
  };

  return { tracks, loading, error, fetchTracks };
};

export default useFetchTracks;
