import { useState } from "react";
import axios from "axios";

const SearchResults = ({ albums, artistName, onAddToLibrary }) => {
  const [expandedAlbumId, setExpandedAlbumId] = useState(null);
  const [tracks, setTracks] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const toggleTracks = async (albumId) => {
    if (expandedAlbumId === albumId) {
      // Si ya estaba abierto, lo cerramos
      setExpandedAlbumId(null);
    } else {
      setExpandedAlbumId(albumId);

      // Si aún no se han cargado las canciones de este álbum
      if (!tracks[albumId]) {
        setLoading(true);
        setError(null);

        try {
          const response = await axios.get(
            `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}/tracks`
          );

          setTracks((prevTracks) => ({
            ...prevTracks,
            [albumId]: response.data.data,
          }));
        } catch (err) {
          console.error("Error al obtener las canciones:", err);
          setError("No se pudieron cargar las canciones.");
        } finally {
          setLoading(false);
        }
      }
    }
  };

  return (
    <div className="search-results">
      {albums.map((album) => (
        <div key={album.id} className="album-card">
          <img src={album.cover_medium} alt={album.title} />
          <p>{artistName}</p>
          <h3>{album.title}</h3>

          <button onClick={() => onAddToLibrary({ album, artistName })}>
            Agregar a biblioteca
          </button>

          <button onClick={() => toggleTracks(album.id)}>
            {expandedAlbumId === album.id ? "Ocultar canciones" : "Ver canciones"}
          </button>

          {/* Lista de canciones visible solo si este álbum está expandido */}
          {expandedAlbumId === album.id && (
            <div className="track-list">
              {loading && <p>Cargando canciones...</p>}
              {error && <p style={{ color: "red" }}>{error}</p>}
              {tracks[album.id] && (
                <ul>
                  {tracks[album.id].map((track) => (
                    <li key={track.id}>
                      {track.title}
                      {/* Opcional: agregar preview */}
                      {track.preview && (
                        <audio controls src={track.preview} style={{ marginLeft: "10px" }} />
                      )}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default SearchResults;
