import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';

function SongDetail() {
  const { trackId } = useParams();
  const [track, setTrack] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrack = async () => {
      setLoading(true);
      setError(null);
      try {
        
        const response = await axios.get(
          `https://cors-anywhere.herokuapp.com/https://api.deezer.com/track/${trackId}`
        );
      
        setTrack(response.data);
      } catch (err) {
        setError('Error al cargar los detalles de la canción');
      } finally {
        setLoading(false);
      }
    };

    fetchTrack();
  }, [trackId]);

  if (loading) return <p>Cargando detalles...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      {track && (
        <>
          <img src={track.album.cover} alt={track.album.title} />
          <h2>{track.title}</h2>
          <p>Artista: {track.artist.name}</p>
          <p>Álbum: {track.album.title}</p>
          <audio controls="true"  source src={track.preview} />
        </>
      )}
    </div>
  );
}

export default SongDetail;
