import './components/Header/styles.css';
import './components/SearchResults/styles.css';
import './components/Song/styles.css';
import './components/Library/styles.css';

import React, { useState } from 'react';
import Header from './components/Header';
import Library from './components/Library';
import SearchResults from './components/SearchResults';
import axios from 'axios';

const App = () => {
  const [search, setSearch] = useState('');
  const [artists, setArtists] = useState([]);
  const [albums, setAlbums] = useState([]);
  const [selectedArtistName, setSelectedArtistName] = useState('');
  const [librarySongs, setLibrarySongs] = useState([]);

  const proxy = 'https://cors-anywhere.herokuapp.com/';

  const handleSearch = async () => {
    if (!search) return;

    try {
      const response = await axios.get(`${proxy}https://api.deezer.com/search/artist?q=${search}`);
      setArtists(response.data.data);
      setAlbums([]);
      setSelectedArtistName('');
    } catch (error) {
      console.error('Error al buscar artistas:', error);
    }
  };

  const fetchAlbums = async (artistId, artistName) => {
    try {
      const response = await axios.get(`${proxy}https://api.deezer.com/artist/${artistId}/albums`);
      console.log('Respuesta de 치lbumes:', response.data);
      const albumsData = response.data.data.map((album) => ({
        id: album.id,
        image: album.cover_medium,
        album: album.title,
        artist: artistName,
        releaseDate: album.release_date,
      }));
      setAlbums(albumsData);
      setSelectedArtistName(artistName);
    } catch (error) {
      console.error('Error al obtener 치lbumes:', error);
    }
  };

  const handleAddToLibrary = (song) => {
    if (!librarySongs.some((s) => s.id === song.id)) {
      setLibrarySongs([...librarySongs, song]);
    }
  };

  return (
    <div className='App p-4 font-sans'>
      <Header />


      {artists.length > 0 && (
        <ArtistList artists={artists} onSelect={fetchAlbums} />
      )}

      {albums.length > 0 && (
        <SearchResults songs={albums} onAddToLibrary={handleAddToLibrary} />
      )}

      <Library songs={librarySongs} />
    </div>
  );
};


export default App;
