import { Link } from "react-router-dom";
import useFetchAlbums from "../../hooks/useFetchAlbums";


const SearchResults = ({ albums, artistName, onAddToLibrary }) => {
  const { selectedAlbumId, tracks, loading, error, toggleTracks } = useFetchAlbums();

  return (
    <section>
      {albums.map((album) => (
        <article key={album.id}>
          <img src={album.cover_medium} alt={album.title} />
          <p>{artistName}</p>
          <h3>{album.title}</h3>

          <button onClick={() => onAddToLibrary({ album, artistName })}>
            Agregar a biblioteca
          </button>

          <button onClick={() => toggleTracks(album.id)}>
            {selectedAlbumId === album.id ? "Ocultar canciones" : "Ver canciones"}
          </button>

          {selectedAlbumId === album.id && (
            <div>
              {loading ? (
                <p>Cargando canciones...</p>
              ) : error ? (
                <p>Error cargando canciones</p>
              ) : (
                <ul>
                  {tracks[album.id]?.map((track) => (
                    <li key={track.id}>
                      <Link to={`/song/${track.id}`}>{track.title}</Link>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          )}
        </article>
      ))}
    </section>
  );
};

export default SearchResults;
