import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function SongDetail() {
  const { albumId } = useParams(); // Asegúrate que en la ruta está como /song/:albumId
  const [album, setAlbum] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchAlbumDetails = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}`
      );
      setAlbum(response.data);
    } catch (err) {
      setError('No se pudieron cargar los detalles del álbum.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAlbumDetails();
  }, [albumId]);

  if (loading) return <p>Cargando detalles...</p>;
  if (error) return (
    <div>
      <p>{error}</p>
      <button onClick={fetchAlbumDetails}>Reintentar</button>
    </div>
  );

  return (
    <div className="song-detail">
      <img src={album.cover_medium} alt={album.title} />
      <h2>{album.title}</h2>
      <p>Artista: {album.artist.name}</p>
      <p>Fecha de lanzamiento: {album.release_date}</p>

      <h3>Lista de canciones</h3>
      <ol>
        {album.tracks.data.map((track) => (
          <li key={track.id}>
            {track.title}
            <br />
            <audio controls src={track.preview}></audio>
          </li>
        ))}
      </ol>
    </div>
  );
}

export default SongDetail;
