const { default: styled } = require("styled-components");


const SongDetailsCont = styled.section `
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 2%;
`

const SongCover = styled.img `
    width: 230px;
`

const TrackTitle = styled.h2 `
    text-align: center;
`

export {
    SongDetailsCont,
    SongCover,
    TrackTitle,

}
