import React, { useState } from "react";
import axios from "axios";

const Album = ({ image, artist, album, id, onAdd }) => {
  const [open, setOpen] = useState(false);
  const [tracks, setTracks] = useState([]);

  const handleAlbumClick = async () => {
    if (open) {
      setOpen(false);
      setTracks([]);
      return;
    }

    try {
      const response = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${id}/tracks`
      );
      setTracks(response.data.data);
      setOpen(true);
    } catch (error) {
      console.log("error al obtener canciones", error);
    }
  };

  return (
    <section>
      <article>
        <img alt="img album" src={image} />
        <h2>{artist}</h2>
        <p>{album}</p>

        <a onClick={handleAlbumClick} style={{ cursor: "pointer", color: "blue" }}>
          {open ? "Ocultar canciones" : "Mostrar canciones"}
        </a>

        {/* Mostrar bot√≥n solo si existe onAdd */}
        {onAdd && (
          <button onClick={() => onAdd({ image, artist, album, id })}>
            Add to Library
          </button>
        )}

        {/* Mostrar canciones si open es true */}
        {open && (
          <ul>
            {tracks.map((track) => (
              <li key={track.id}>{track.title}</li>
            ))}
          </ul>
        )}
      </article>
    </section>
  );
};

export default Album;
