import './components/Header/styles.css';
import './components/SearchResults/styles.css';
import './components/Library/styles.css';
import './components/SearchInput/styles.css';
import './components/ArtistList/styles.css';
import './components/SongDetail/styles.css';

import React, { useState } from 'react';
import Header from './components/Header';
import Library from './components/Library';
import SearchResults from './components/SearchResults';
import ArtistList from './components/ArtistList';
import SearchInput from './components/SearchInput';
import SongDetail from './components/SongDetail';
import { Route, Routes, useNavigate } from 'react-router-dom';

import useArtistSearch from './hooks/useArtistSearch';
import useFetchAlbums from './hooks/useFetchAlbums';

const App = () => {
  const [library, setLibrary] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedArtistName, setSelectedArtistName] = useState('');
  const [message, setMessage] = useState('');

  const {
    artists,
    searchArtists,
    loading: loadingArtists,
    error: errorArtists,
    success: successArtists
  } = useArtistSearch();

  const {
    albums,
    fetchAlbums,
    loading: loadingAlbums,
    error: errorAlbums,
    succes: successAlbums,
  } = useFetchAlbums();

  const navigate = useNavigate();

  const handleSearch = () => {
    searchArtists(search);
    setSelectedArtistName('');
  };

  const handleSelectArtist = async (id, name) => {
    setSelectedArtistName(name);
    await fetchAlbums(id, name);
    navigate('/SearchResults');
  };

  // Función para limpiar estados y volver a home
  const handleGoHome = () => {
    setSelectedArtistName('');
    // Aquí si quieres limpiar albums, tendrías que agregar una función en useFetchAlbums para resetear albums, 
    // o reiniciar la app de otra forma si no hay.
    // Por ejemplo, recargar la página:
    // window.location.href = '/';
    navigate('/');
  };

  const handleAddToLibrary = ({ album, artistName }) => {
    if (!library.some((item) => item.id === album.id)) {
      setLibrary([
        ...library,
        {
          id: album.id,
          album: album.title,
          artist: artistName,
          image: album.cover_medium,
        },
      ]);
      setMessage("Agregado a Libreria");
      setTimeout(() => setMessage(""), 1500);
    }
  };

  return (
    <main>
      <Header onGoHome={handleGoHome} />

      {message && <h1 className="addToLibrary">{message}</h1>}

      <Routes>
        <Route
          path="/"
          element={
            <>
              <SearchInput
                search={search}
                setSearch={setSearch}
                handleSearch={handleSearch}
              />

              {loadingArtists && <p>Buscando artistas...</p>}
              {errorArtists && <p>{errorArtists}</p>}
              {successArtists && artists.length === 0 && <p>No se encontraron artistas.</p>}

              {artists.length > 0 && !selectedArtistName && (
                <ArtistList
                  artists={artists}
                  onSelect={handleSelectArtist}
                />
              )}
              {/* Aquí ya NO mostramos SearchResults en home */}
            </>
          }
        />

        <Route
          path="/SearchResults"
          element={
            <>
              {albums.length > 0 && selectedArtistName && (
                <SearchResults
                  albums={albums}
                  artistName={selectedArtistName}
                  onAddToLibrary={handleAddToLibrary}
                />
              )}
            </>
          }
        />

        <Route path="/song/:id" element={<SongDetail />} />

        <Route path="/library" element={<Library library={library} />} />
      </Routes>
    </main>
  );
};

export default App;
