import styled from "styled-components";
import Theme from "../../theme";


const SearchResutlsContainter = styled.section `
    margin: 2%;
    background: rgba(255, 255, 255, 0.27);
    border-radius: 16px;
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
    backdrop-filter: blur(11.3px);
    border: 3px solid rgba(179, 179, 179, 0.493);
    align-items: center;
`

const Albums = styled.article `
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    margin: 2%;
    justify-content: space-between;

`

const ImgAlbum = styled.img `
    border-radius: 50%;
    width: 80px;
`
const ArtistName = styled.h1 `
    text-align: center;
` 

const ShowSongs = styled.button `
    height: 40px;
    width: 100px;
    font-size: 1.5vh;
    font-weight: bolder;
    color: ${props => props.theme.colors.secondary};
    background:transparent;
    border-radius: 16px;
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
    backdrop-filter: blur(11.3px);
    border: 1px solid rgba(255, 255, 255, 0.705);
    align-items: center;
    cursor: pointer;
    transition: ease-out 0.8s;

    &:hover{
        scale: 1.2;
        transition: ease-out 0.2s;
    }

`

export {
    SearchResutlsContainter,
    ImgAlbum,
    ArtistName,
    Albums,
    ShowSongs,

}
