import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import IconList from '../SearchResults/img/songListBlack.png'

const Library = ({ library }) => {
  const [selectedAlbumId, setSelectedAlbumId] = useState(null);
  const [tracks, setTracks] = useState({});
  const [loading, setLoading] = useState(false);

  const toggleTracks = async (albumId) => {
    if (selectedAlbumId === albumId) {
      setSelectedAlbumId(null);
    } else {
     
      setSelectedAlbumId(albumId);
      if (!tracks[albumId]) {
        setLoading(true);
        try {
          const url = `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}/tracks`;
          const response = await axios.get(url);
          setTracks((prev) => ({ ...prev, [albumId]: response.data.data }));
        } catch (error) {
          console.error("Error al cargar canciones:", error);
        } finally {
          setLoading(false);
        }
      }
    }
  };

  if (!library || library.length === 0) {
    return <i>nada por aqui</i>;
  }

  return (
    <section className="library">
      <h2>Tu Biblioteca</h2>
      <ul className="library-list">
        {library.map((item, index) => (
          <li key={index} className="library-item">
          <img src={item.image} alt={item.album} width={64} />


            <article>
              <h1>{item.artist}</h1>
              <p
              onClick={() => toggleTracks(item.id)}>{item.album}</p>
              
              
              <img 
                
                src={IconList}
              />
              

              
              {selectedAlbumId === item.id && (
                <article className="track-list">
                  {loading ? (
                    <p>Cargando canciones...</p>
                  ) : (
                    <ul>
                      {(tracks[item.id] || []).map((track) => (
                        <li key={track.id}>
                        <Link to={`/song/${track.id}`}>{track.title}</Link>
                        </li>
                        
                      ))}
                    </ul>
                  )}
                </article>
              )}
            </article>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default Library;
