import { useState, useEffect } from "react";
import axios from "axios";

const useFetchAlbumDetail = (albumId) => {
  const [album, setAlbum] = useState(null);
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchAlbum = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}`
      );
      const albumData = response.data;
      setAlbum(albumData);
      setTracks(albumData.tracks.data);
    } catch (err) {
      setError("Error al obtener detalles del álbum");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (albumId) fetchAlbum();
  }, [albumId]);

  return { album, tracks, loading, error };
};

export default useFetchAlbumDetail;
