import axios from "axios";


const SearchResults = ({ albums, onAddToLibrary, onAlbumSelect }) => {
  // ...

  const handleAlbumClick = async (albumId) => {
    if (selectedAlbumId === albumId) {
      setSelectedAlbumId(null);
      setTracks([]);
      return;
    }

    try {
      const response = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${albumId}/tracks`
      );
      setTracks(response.data.data);
      setSelectedAlbumId(albumId);

      // Ocultar la lista de resultados
      onAlbumSelect(); // ðŸ‘ˆ
    } catch (error) {
      console.error("Error al obtener canciones del Ã¡lbum:", error);
    }
  };
 }

  export default SearchResults;
