import { useParams } from 'react-router-dom';
import useFetchAlbumDetail from '../../hooks/useFetchAlbumDetail';

function SongDetail() {
  const { id } = useParams();
  const { album, tracks, loading, error, retry } = useFetchAlbumDetail(id);

  return (
    <div>
      {loading && <p>Cargando detalles del álbum...</p>}
      {error && (
        <div>
          <p>{error}</p>
          <button onClick={retry}>Reintentar</button>
        </div>
      )}
      {album && (
        <>
          <h2>{album.title}</h2>
          <p>Artista: {album.artist.name}</p>
          <p>Año: {album.release_date}</p>
          <img src={album.cover_medium} alt={album.title} />

          <h3>Lista de canciones</h3>
          <ul>
            {tracks.map((track) => (
              <li key={track.id}>{track.title}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default SongDetail;
