import { useParams } from 'react-router-dom';
import { useFetchAlbums } from '../hooks/useFetchAlbums';

function SongDetail() {
  const { id } = useParams();
  const url = `https://theaudiodb.com/api/v1/json/2/album.php?m=${id}`;
  const { data, loading, error, retry } = useFetchAlbums(url);

  const album = data?.album?.[0];

  return (
    <div>
      {loading && <p>Cargando detalles...</p>}
      {error && (
        <div>
          <p>{error}</p>
          <button onClick={retry}>Reintentar</button>
        </div>
      )}
      {album && (
        <>
          <h2>{album.strAlbum}</h2>
          <p>Artista: {album.strArtist}</p>
          <p>AÃ±o: {album.intYearReleased}</p>
          <img src={album.strAlbumThumb} alt={album.strAlbum} />
        </>
      )}
    </div>
  );
}

export default SongDetail;
