import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';

function SongDetail() {
  const { trackId } = useParams();
  const [track, setTrack] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!trackId) return;

    setLoading(true);
    setError(null);

    axios.get(`https://cors-anywhere.herokuapp.com/https://api.deezer.com/track/${trackId}`)
      .then(response => {
        setTrack(response.data);
      })
      .catch(() => setError('Error al cargar los detalles de la canción'))
      .finally(() => setLoading(false));
  }, [trackId]);

  if (loading) return <p>Cargando detalles...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      {track ? (
        <>
          <h2>{track.title}</h2>
          {track.artist && track.artist.name ? (
            <p>Artista: {track.artist.name}</p>
          ) : (
            <p>Artista: No disponible</p>
          )}

          {track.album && track.album.title ? (
            <p>Álbum: {track.album.title}</p>
          ) : (
            <p>Álbum: No disponible</p>
          )}

          {track.album && track.album.cover ? (
            <img src={track.album.cover} alt={track.album.title} />
          ) : null}
        </>
      ) : (
        <p>No se encontró la canción</p>
      )}
    </div>
  );
}

export default SongDetail;
