import React, { useState } from "react";
import axios from "axios";

const Album = ({ image, artist, album, id, onAdd }) => {
  const [open, setOpen] = useState(false);
  const [tracks, setTracks] = useState([]);

  const handleAlbumClick = async () => {
    if (open) {
      setOpen(false);
      setTracks([]);
      return;
    }

    try {
      const response = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://api.deezer.com/album/${id}/tracks`
      );
      setTracks(response.data.data);
      setOpen(true);
    } catch (error) {
      console.log("error al obtener canciones", error);
    }
  };

  return (
    <section>
      <article>
        <img alt="img album" src={image} />
        <h2>{artist}</h2>
        <p>{album}</p>

        {/* Bot√≥n para mostrar/ocultar canciones */}
        <button onClick={handleAlbumClick} style={{ marginTop: "10px" }}>
          {open ? "Ocultar canciones" : "Mostrar canciones"}
        </button>

        {/* Mostrar canciones abajo del bot√≥n */}
        {open && (
          <ul style={{ marginTop: "10px" }}>
            {tracks.map((track) => (
              <li key={track.id}>üéµ {track.title}</li>
            ))}
          </ul>
        )}

        {/* Bot√≥n para agregar a la biblioteca */}
        {onAdd && (
          <button
            onClick={() => onAdd({ image, artist, album, id })}
            style={{ marginTop: "10px", backgroundColor: "green", color: "white" }}
          >
            Add to Library
          </button>
        )}
      </article>
    </section>
  );
};

export default Album;
